import Menu from "../../components/menu/menu.jsx";
//import Busca from "../../components/busca/busca.jsx";
import Indicador from "../../components/indicador/indicador.jsx";
import Grafico from "../../components/grafico/grafico.jsx";
import { useEffect, useState } from "react";
import api from "../../services/api.js";

function Movimento(){

    const [dadosIndicadores, setDadosIndicadores] = useState({
        "valor_total_mes": 0,
        "qvalor_total_dia": 0,
        "valor_cartao": 0,
        "valor_pix": 0
    });

    const [dadosAnual, setDadosAnual] = useState([["Mês", "Valor"], [0, 0]]);

    
    function MontarIndicadores(){
        
        api.get("/dashboard/movimento?id_usuario=" + localStorage.getItem("id_usuario"))
        .then((resp) => {
            setDadosIndicadores(resp.data);
        })
        .catch((err) => {
            setDadosIndicadores({
                "valor_total_mes": 0,
                "qvalor_total_dia": 0,
                "valor_cartao": 0,
                "valor_pix": 0
            });
            alert("Erro ao carregar indicadores");
        });
    }

    function MontarGrafAnual(){
        
        api.get("/dashboard/movementomensal")
        .then((resp) => {
            setDadosAnual(resp.data);
        })
        .catch((err) => {
            setDadosAnual([["Mês", "Valor"], [0, 0]]);
            alert("Erro ao carregar gráfico");
        });
        
    }

    function MontarDashboard(){
        MontarIndicadores();
        MontarGrafAnual();
    }

    useEffect(() => {        
        MontarDashboard();
    }, []);


    return <>
        <div className="container-fluid">
            <div className="row flex-nowrap">
                <div className="col-auto col-md-3 col-xl-2 px-sm-2 px-0">
                    <Menu page="dashboard" />
                </div>

                <div className="col py-3 me-3">
                    
                    
                    <div className="d-flex justify-content-between"> 
                        <h1>Dashboard</h1>
                        <button onClick={MontarDashboard} className="btn btn-primary ms-4">Atualizar</button>
                    </div>

                    <div className="row">
                        <div className="col-md-3 mt-4">
                            <Indicador titulo="Total de venda no mês"
                                       valor={new Intl.NumberFormat('pt-BR', {style: 'currency', currency: 'BRL'}).format(dadosIndicadores.valor_mes)}
                                       rodape={`${dadosIndicadores.valor_total_mes} atividades`} />
                        </div>                    

                        <div className="col-md-3 mt-4">
                            <Indicador titulo="Total de vendas hoje"
                                       valor={`${dadosIndicadores.valor_total_dia} atividades`}
                                       rodape={new Intl.NumberFormat('pt-BR', {style: 'currency', currency: 'BRL'}).format(dadosIndicadores.valor_dia)} />
                        </div>

                        <div className="col-md-3 mt-4">
                            <Indicador titulo="Total Cartão"
                                       valor={`${dadosIndicadores.valor_cartao} atividades`}
                                       rodape={new Intl.NumberFormat('pt-BR', {style: 'currency', currency: 'BRL'}).format(dadosIndicadores.valor_dia)} />
                        </div>

                        <div className="col-md-3 mt-4">
                            <Indicador titulo="Total Pix"
                                       valor={`${dadosIndicadores.valor_pix} atividades`}
                                       rodape={new Intl.NumberFormat('pt-BR', {style: 'currency', currency: 'BRL'}).format(dadosIndicadores.valor_dia)} />
                        </div>

                        <div className="col-12 mt-5">
                            <Grafico titulo="Vendas Mensal"
                                     chartType="Line"
                                     dados={dados_mensal}                                     
                                      />
                        </div>
                        
                    </div>
                    
                </div>
            </div>
        </div>
    </>
}

export default Dashboard;
